package ComcaveProgramme;

public class BoolscheTabelle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int ersteZeile=1;ersteZeile<=10;ersteZeile++){//Zeilenläufer
			System.out.print("\th" + ersteZeile);
		}
		for(int x =1;x<=10;x++){
			System.out.print("\n" + x);
			for(int y =1;y<=10;y++){
				System.out.print("\t" + (y*x));

	}

}
	}}
